import axios from 'axios';
import { AUTH_LOGIN, AUTH_LOGOUT, AUTH_REGISTER, AUTH_ERROR, CONTACT_INQUIRY_SUCCESS, CONTACT_INQUIRY_ERROR } from '../types';

const API_URL = 'http://localhost:5000/api/auth';

// Action to register a new user
export const registerUser = (userData) => async (dispatch) => {
    try {
        const response = await axios.post(`${API_URL}/signup`, userData);
        dispatch({ type: AUTH_REGISTER, payload: response.data });
    } catch (error) {
        dispatch({ 
            type: AUTH_ERROR, 
            payload: error.response?.data?.message || "Registration failed" 
        });
    }
};

// Action to log in a user
export const loginUser = (credentials) => async (dispatch) => {
    try {
        const response = await axios.post(`${API_URL}/login`, credentials);
        localStorage.setItem('token', response.data.token); // Store token
        dispatch({ type: AUTH_LOGIN, payload: response.data });
    } catch (error) {
        dispatch({ 
            type: AUTH_ERROR, 
            payload: error.response?.data?.message || "Login failed" 
        });
    }
};

// Action to log out a user
export const logoutUser = () => (dispatch) => {
    localStorage.removeItem('token'); // Remove token
    dispatch({ type: AUTH_LOGOUT });
};

// Action to send contact inquiry
export const sendContactInquiry = (formData) => async (dispatch) => {
    try {
        const response = await axios.post('http://localhost:5000/api/contact', formData);
        dispatch({ type: CONTACT_INQUIRY_SUCCESS, payload: response.data });
    } catch (error) {
        dispatch({ 
            type: CONTACT_INQUIRY_ERROR, 
            payload: error.response?.data?.message || "Failed to send inquiry" 
        });
    }
};
