import { useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import { gsap } from "gsap";
import "../pages/Home.css";

const Home = () => {
  const heroRef = useRef(null);
  const titleRef = useRef(null);
  const descriptionRef = useRef(null);
  const buttonRef = useRef(null);

  useEffect(() => {
    gsap.from(heroRef.current, { opacity: 0, y: 50, duration: 1, ease: "power3.out" });
    gsap.from(titleRef.current, { opacity: 0, y: -30, duration: 1, delay: 0.5, ease: "power3.out" });
    gsap.from(descriptionRef.current, { opacity: 0, y: 30, duration: 1, delay: 0.7, ease: "power3.out" });
    gsap.from(buttonRef.current, { opacity: 0, scale: 0.5, duration: 0.8, delay: 1, ease: "back.out(1.7)" });
  }, []);

  return (
    <div className="bg-gray-900 text-white min-h-screen flex flex-col">
      {/* Navbar */}
      <header className="navbar fixed top-0 left-0 w-full p-6 flex justify-between items-center shadow-lg">
        <h1 className="text-3xl font-bold">Doberman Breeding</h1>
        <nav className="space-x-6">
          <Link to="/about" className="hover:text-gray-400 transition">About Us</Link>
          <Link to="/available-puppies" className="hover:text-gray-400 transition">Available Puppies</Link>
          <Link to="/gallery" className="hover:text-gray-400 transition">Gallery</Link>
          <Link to="/testimonials" className="hover:text-gray-400 transition">Testimonials</Link>
          <Link to="/blog" className="hover:text-gray-400 transition">Blog</Link>
          <Link to="/contact" className="hover:text-gray-400 transition">Contact</Link>
        </nav>
      </header>

      {/* Hero Section */}
      <main ref={heroRef} className="hero-container">
        <h2 ref={titleRef} className="hero-title">
          Welcome to Our Doberman Family
        </h2>
        <p ref={descriptionRef} className="hero-description">
          We are dedicated to breeding **healthy**, **happy Dobermans** with excellent temperaments. Join our loving Doberman family today!
        </p>
        <Link ref={buttonRef} to="/available-puppies" className="hero-button mt-6">
          View Available Puppies
        </Link>
      </main>

      {/* Animated Background Glows */}
      <div className="background-glow glow-1"></div>
      <div className="background-glow glow-2"></div>

      {/* Footer */}
      <footer className="bg-gray-800 p-4 text-center shadow-md">
        <p>&copy; {new Date().getFullYear()} Doberman Breeding. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
