import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { sendContactInquiry } from '../redux/actions/authActions';

const Contact = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        message: ''
    });

    const dispatch = useDispatch();

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(sendContactInquiry(formData));
        setFormData({ name: '', email: '', phone: '', message: '' });
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 text-white">
            <h1 className="text-3xl font-bold mb-6">Contact Us</h1>
            <form onSubmit={handleSubmit} className="w-full max-w-lg bg-gray-800 p-8 rounded-lg shadow-md">
                {['name', 'email', 'phone', 'message'].map((field) => (
                    <div key={field} className="mb-4">
                        <label className="block text-sm font-medium mb-2 capitalize" htmlFor={field}>
                            {field}
                        </label>
                        {field === 'message' ? (
                            <textarea
                                name={field}
                                value={formData[field]}
                                onChange={handleChange}
                                required
                                className="w-full p-2 border border-gray-600 rounded bg-gray-700 text-white"
                            />
                        ) : (
                            <input
                                type={field === 'email' ? 'email' : field === 'phone' ? 'tel' : 'text'}
                                name={field}
                                value={formData[field]}
                                onChange={handleChange}
                                required
                                className="w-full p-2 border border-gray-600 rounded bg-gray-700 text-white"
                            />
                        )}
                    </div>
                ))}
                <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                    Send Message
                </button>
            </form>
        </div>
    );
};

export default Contact;
