import React from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";
import logo from "../assets/logo.png";
import heroImage from "../assets/hero-doberman.jpg";
import Kid from "../assets/kid.jpg";
import Mother from "../assets/Mother.jpg";
import Father from "../assets/Father.jpg";

const Home = () => {
  return (
    <div className="bg-gray-900 text-white min-h-screen flex flex-col">
      {/* Navbar */}
      <header className="flex justify-between items-center px-6 py-4 bg-gray-800 shadow-md">
        <div className="flex items-center">
          <img src={logo} alt="Logo" className="h-12 w-auto mr-4" />
          <h1 className="text-3xl font-bold">Haithem Doberman Breeding</h1>
        </div>
        <nav className="hidden md:flex">
          {[
            "about", "available-puppies", "gallery", "testimonials", "blog", "contact"
          ].map((item) => (
            <Link
              key={item}
              to={`/${item}`}
              className="mx-4 hover:text-gray-400 transition duration-300"
            >
              {item.replace("-", " ").toUpperCase()}
            </Link>
          ))}
        </nav>
      </header>

      {/* Hero Section */}
      <section className="relative w-full h-[500px] flex items-center justify-center text-center">
        <motion.img
          src={heroImage}
          alt="Doberman Hero"
          className="absolute inset-0 w-full h-full object-cover opacity-50"
          initial={{ scale: 1.2 }}
          animate={{ scale: 1 }}
          transition={{ duration: 1.5 }}
        />
        <div className="relative z-10">
          <motion.h2
            className="text-6xl font-bold mb-4"
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1 }}
          >
            Welcome to Our Doberman Family
          </motion.h2>
          <p className="text-lg mb-6 max-w-2xl mx-auto">
            We breed healthy, happy Dobermans with excellent temperaments.
          </p>
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5, delay: 0.5 }}
          >
            <Link
              to="/available-puppies"
              className="bg-blue-600 text-white py-3 px-6 rounded-lg text-lg hover:bg-blue-500 transition"
            >
              View Available Puppies
            </Link>
          </motion.div>
        </div>
      </section>

      {/* Our Dobermans Section */}
      <section className="container mx-auto py-16 px-6">
        <h3 className="text-4xl font-bold text-center mb-8">Our Dobermans</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {[{ img: Father, title: "Champion Bloodline" },
            { img: Mother, title: "Strong & Intelligent" },
            { img: Kid, title: "Loyal & Protective" }].map((dog, index) => (
            <motion.div
              key={index}
              className="bg-gray-800 p-4 rounded-lg shadow-lg flex flex-col items-center"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: index * 0.3 }}
            >
              <img
                src={dog.img}
                alt={dog.title}
                className="w-full h-auto max-h-[500px] object-cover rounded-lg"
              />
              <h4 className="text-xl font-semibold mt-4">{dog.title}</h4>
              <p className="text-gray-300 mt-2 text-center">
                Lorem ipsum dolor sit amet consectetur adipisicing elit.
              </p>
            </motion.div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="bg-gray-800 p-6 text-center mt-12">
        <p>&copy; {new Date().getFullYear()} Doberman Breeding Business. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
