import axios from 'axios';
import { 
    FETCH_PUPPIES_REQUEST, 
    FETCH_PUPPIES_SUCCESS, 
    FETCH_PUPPIES_FAILURE, 
    ADD_PUPPY_REQUEST, 
    ADD_PUPPY_SUCCESS, 
    ADD_PUPPY_FAILURE, 
    UPDATE_PUPPY_REQUEST, 
    UPDATE_PUPPY_SUCCESS, 
    UPDATE_PUPPY_FAILURE, 
    DELETE_PUPPY_REQUEST, 
    DELETE_PUPPY_SUCCESS, 
    DELETE_PUPPY_FAILURE 
} from '../constants/puppyConstants';

// Fetch puppies
export const fetchPuppies = () => async (dispatch) => {
    dispatch({ type: FETCH_PUPPIES_REQUEST });
    try {
        const { data } = await axios.get('/api/puppies');
        dispatch({ type: FETCH_PUPPIES_SUCCESS, payload: data });
    } catch (error) {
        dispatch({ type: FETCH_PUPPIES_FAILURE, payload: error.response.data.message });
    }
};

// Add a puppy
export const addPuppy = (puppyData) => async (dispatch) => {
    dispatch({ type: ADD_PUPPY_REQUEST });
    try {
        const { data } = await axios.post('/api/puppies', puppyData);
        dispatch({ type: ADD_PUPPY_SUCCESS, payload: data });
    } catch (error) {
        dispatch({ type: ADD_PUPPY_FAILURE, payload: error.response.data.message });
    }
};

// Update a puppy
export const updatePuppy = (puppyId, puppyData) => async (dispatch) => {
    dispatch({ type: UPDATE_PUPPY_REQUEST });
    try {
        const { data } = await axios.put(`/api/puppies/${puppyId}`, puppyData);
        dispatch({ type: UPDATE_PUPPY_SUCCESS, payload: data });
    } catch (error) {
        dispatch({ type: UPDATE_PUPPY_FAILURE, payload: error.response.data.message });
    }
};

// Delete a puppy
export const deletePuppy = (puppyId) => async (dispatch) => {
    dispatch({ type: DELETE_PUPPY_REQUEST });
    try {
        await axios.delete(`/api/puppies/${puppyId}`);
        dispatch({ type: DELETE_PUPPY_SUCCESS, payload: puppyId });
    } catch (error) {
        dispatch({ type: DELETE_PUPPY_FAILURE, payload: error.response.data.message });
    }
};
